import React, { useState, useEffect, useRef } from 'react';
import '../styles/InputPage.css';

const InputPage = () => {
    const [startLocation, setStartLocation] = useState('');
    const [endLocation, setEndLocation] = useState('');
    const [transportMode, setTransportMode] = useState('subways');
    const [startTime, setStartTime] = useState(new Date().toISOString().slice(0, 16));
    const [tripDuration, setTripDuration] = useState('');
    const [wheelchairAccessible, setWheelchairAccessible] = useState(false);

    const handleSubmit = async (e) => {
        e.preventDefault();
        const data = {
            startLocation,
            endLocation,
            transportMode,
            startTime,
            tripDuration,
            wheelchairAccessible,
        };

        const response = await fetch('http://localhost:5000/api/itinerary', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(data),
        });

        if (response.ok) {
            const result = await response.json();
            console.log('Success:', result);
        } else {
            console.error('Error:', response.statusText);
        }
    };

    const handleGetLocation = () => {
        if (navigator.geolocation) {
            navigator.geolocation.getCurrentPosition(
                (position) => {
                    const { latitude, longitude } = position.coords;
                    setStartLocation(`${latitude}, ${longitude}`);
                },
                (error) => {
                    alert('Unable to retrieve your location.');
                }
            );
        } else {
            alert('Geolocation is not supported by your browser.');
        }
    };

    return (
        <div className="input-page">
            <h1>TravelBuddyAI</h1>
            <form onSubmit={handleSubmit}>
                <label>
                    Start Location:
                    <input
                        type="text"
                        value={startLocation}
                        onChange={(e) => setStartLocation(e.target.value)}
                        required
                    />
                    <button type="button" onClick={handleGetLocation} style={{marginLeft: '10px'}}>Use Current Location</button>
                </label>
                <label>
                    End Location:
                    <input
                        type="text"
                        value={endLocation}
                        onChange={(e) => setEndLocation(e.target.value)}
                    />
                </label>
                <label>
                    Mode of Transport:
                    <select
                        value={transportMode}
                        onChange={(e) => setTransportMode(e.target.value)}
                    >
                        <option value="subways">Subways</option>
                        <option value="buses">Buses</option>
                        <option value="taxis">Taxis</option>
                        <option value="e-bikes">E-bikes</option>
                    </select>
                </label>
                <label>
                    Start Time:
                    <input
                        type="datetime-local"
                        value={startTime}
                        onChange={(e) => setStartTime(e.target.value)}
                    />
                </label>
                <label>
                    Trip Duration (in minutes):
                    <input
                        type="number"
                        value={tripDuration}
                        onChange={(e) => setTripDuration(e.target.value)}
                    />
                </label>
                <label>
                    Wheelchair Accessible:
                    <input
                        type="checkbox"
                        checked={wheelchairAccessible}
                        onChange={(e) => setWheelchairAccessible(e.target.checked)}
                    />
                </label>
                <button type="submit">Plan Itinerary</button>
            </form>
        </div>
    );
};

export default InputPage;
